  {{ page.url | absolute_url | xml_escape }}

  {% assign title = site.title | default: site.name %}
  {% if page.collection != "posts" %}
    {% assign collection = page.collection | capitalize %}
    {% assign title = title | append: " | " | append: collection %}
  {% endif %}
  {% if page.category %}
    {% assign category = page.category | capitalize %}
    {% assign title = title | append: " | " | append: category %}
  {% endif %}

  {% if title %}
    {{ title | smartify | xml_escape }}
  {% endif %}

  {% if site.description %}
    {{ site.description | xml_escape }}
  {% endif %}

  {% if site.author %}

        {{ site.author.name | default: site.author | xml_escape }}
      {% if site.author.email %}
        {{ site.author.email | xml_escape }}
      {% endif %}
      {% if site.author.uri %}
        {{ site.author.uri | xml_escape }}
      {% endif %}

  {% endif %}

  {% if page.tags %}
    {% assign posts = site.tags[page.tags] %}
  {% else %}
    {% assign posts = site[page.collection] %}
  {% endif %}
  {% if page.category %}
    {% assign posts = posts | where: "categories", page.category %}
  {% endif %}
  {% unless site.show_drafts %}
    {% assign posts = posts | where_exp: "post", "post.draft != true" %}
  {% endunless %}
  {% assign posts = posts | sort: "published_at" | reverse %}
  {% assign posts_limit = site.feed.posts_limit | default: 10 %}
  {% for post in posts limit: posts_limit %}

      {% assign post_title = post.title | smartify | strip_html | normalize_whitespace | xml_escape %}
      {% assign post_date = post.published_at | default: post.date %}
      {% assign post_image = post.image.path | default: post.image %}

      {{ post_title }}

      {{ post_date | date_to_xmlschema }}
      {{ post.last_modified_at | default: post_date | date_to_xmlschema }}
      {{ post.id | absolute_url | xml_escape }}
      {% assign excerpt_only = post.feed.excerpt_only | default: site.feed.excerpt_only %}
      {% unless excerpt_only %}
      {% capture post_image_tag %}
        {% if post_image %}


          {%- unless post_image contains "://" -%}
          {% picture feed {{ post_image }} --alt {{ post_title | escape }} %}
          {%- else -%}

          {%- endunless -%}

        {% endif %}
      {% endcapture %}
      {% capture post_dateline_tag %}
        {% assign location = post.location | default: site.location | upcase %}
        {% assign dateline = post.date | default: "now" | date: "%-d %b %Y" %}

{{location}} ({{dateline}})
      {% endcapture %}

        {{ post_image_tag | strip }}
        {%- comment -%}
        Posts without a title (mostly Notes) don't get a dateline, so
        that the post content is the first text in the entry
        {%- endcomment -%}
        {%- if post_title != "" -%} 
        {{ post_dateline_tag | strip }}
        {%- endif -%}
        {{ post.content | strip }}
        {% if post.link %}
        <p>
          Link:
          <a href="{{ post.link }}">{{ post.link  | split: '://' | last }}</a>
        </p>
        {% endif %}

      {% endunless %}

      {% assign post_author = post.author | default: post.authors[0] | default: site.author %}
      {% assign post_author = site.data.authors[post_author] | default: post_author %}
      {% assign post_author_email = post_author.email | default: nil %}
      {% assign post_author_uri = post_author.uri | default: nil %}
      {% assign post_author_name = post_author.name | default: post_author %}


          {{ post_author_name | default: "" | xml_escape }}
        {% if post_author_email %}
          {{ post_author_email | xml_escape }}
        {% endif %}
        {% if post_author_uri %}
          {{ post_author_uri | xml_escape }}
        {% endif %}


      {% if post.category %}

      {% elsif post.categories %}
        {% for category in post.categories %}

        {% endfor %}
      {% endif %}

      {% for tag in post.tags %}

      {% endfor %}

      {% assign post_summary = post.description | default: post.excerpt %}
      {% if post_summary and post_summary != empty %}
        {{ post_summary | strip_html | normalize_whitespace }}
      {% endif %}

      {% if post_image %}
      {%- capture post_image_url -%}
      {%- unless post_image contains "://" -%}
      {% picture meta {{ post_image }} %}
      {%- else -%}
      {{ post_image }}
      {% endunless %}
      {%- endcapture -%}


      {% endif %}

  {% endfor %}
